import React, { useState, useEffect } from 'react';

function Register() {
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [latitude, setLatitude] = useState('');
    const [longitude, setLongitude] = useState('');
    const [image, setImage] = useState(null);
    const [preview, setPreview] = useState(null);

    useEffect(() => {
        // Google Maps JavaScript API를 로드합니다.
        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyCLg__BpwjAl8zSJ741LouSvrwuUe__J-Q`;
        script.async = true;
        document.body.appendChild(script);

        // Google Maps API가 로드되면 초기화합니다.
        script.onload = () => {
            const geocoder = new window.google.maps.Geocoder();

            // 주소 입력 필드에 onChange 이벤트를 추가합니다.
            const addressInput = document.getElementById('address');
            addressInput.addEventListener('change', () => {
                geocoder.geocode({ address: addressInput.value }, (results, status) => {
                    if (status === 'OK') {
                        const { lat, lng } = results[0].geometry.location;
                        setLatitude(lat().toString());
                        setLongitude(lng().toString());
                    }
                });
            });
        };

        // 컴포넌트가 언마운트될 때 스크립트를 제거합니다.
        return () => {
            document.body.removeChild(script);
        };
    }, []);

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
        setPreview(URL.createObjectURL(file));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('Name:', name);
        console.log('Address:', address);
        console.log('Latitude:', latitude);
        console.log('Longitude:', longitude);
        console.log('Image:', image);
    };

    const handleReset = () => {
        setName('');
        setAddress('');
        setLatitude('');
        setLongitude('');
        setImage(null);
        setPreview(null);
    };

    return (
        <div>
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Image:
                    <input
                        type="file"
                        onChange={handleImageChange}
                    />
                </label>
                <br />
                {preview && (
                    <div>
                        <img src={preview} alt="Preview" style={{ maxWidth: '200px' }} />
                    </div>
                )}
                <label>
                    Address:
                    <input
                        type="text"
                        id="address"
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                    />
                </label>
                <br />
                <label>
                    Name:
                    <input
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                </label>
                <br />
                <label>
                    Latitude:
                    <input
                        type="text"
                        value={latitude}
                        readOnly
                    />
                </label>
                <br />
                <label>
                    Longitude:
                    <input
                        type="text"
                        value={longitude}
                        readOnly
                    />
                </label>
                <br />
                <button type="submit">Submit</button>
                <button type="button" onClick={handleReset}>Reset</button>
            </form>
        </div>
    );
}

export default Register;
