import React, { useState, useEffect } from 'react';

function Register() {
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [latitude, setLatitude] = useState('');
    const [longitude, setLongitude] = useState('');
    const [image, setImage] = useState(null);
    const [preview, setPreview] = useState(null);


    useEffect(() => {
        const script = document.createElement('script');
        script.src = `https://maps.googleapis.com/maps/api/js?key=AIzaSyCLg__BpwjAl8zSJ741LouSvrwuUe__J-Q`;
        script.async = true;
        document.body.appendChild(script);

        script.onload = () => {
            const geocoder = new window.google.maps.Geocoder();
            const addressInput = document.getElementById('address');
            addressInput.addEventListener('change', () => {
                geocoder.geocode({ address: addressInput.value }, (results, status) => {
                    if (status === 'OK') {
                        const { lat, lng } = results[0].geometry.location;
                        setLatitude(lat().toString());
                        setLongitude(lng().toString());
                    } else {
                        console.error('Geocode was not successful for the following reason: ' + status);
                    }
                });
            });
        };

        return () => {
            document.body.removeChild(script);
        };
    }, []); 

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImage(file);
        setPreview(URL.createObjectURL(file));
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        console.log('Name:', name);
        console.log('Address:', address);
        console.log('Latitude:', latitude);
        console.log('Longitude:', longitude);
        console.log('Image:', image);
    };

    const handleReset = () => {
        setName('');
        setAddress('');
        setLatitude('');
        setLongitude('');
        setImage(null);
        setPreview(null);
    };

    return (
        <div>
            <h1>Register</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    Image:
                    <input type="file" onChange={handleImageChange} />
                </label>
                <br />
                {preview && (
                    <div>
                        <img src={preview} alt="Preview" style={{ maxWidth: '200px' }} />
                    </div>
                )}
                <label>
                    Address:
                    <input type="text" id="address" value={address} onChange={(e) => setAddress(e.target.value)} />
                </label>
                <br />
                <label>
                    Name:
                    <input type="text" value={name} />
                </label>
                <br />
                <label>
                    Latitude:
                    <input type="text" value={latitude} readOnly />
                </label>
                <br />
                <label>
                    Longitude:
                    <input type="text" value={longitude} readOnly />
                </label>
                <br />
                <button type="submit">Submit</button>
                <button type="button" onClick={handleReset}>Reset</button>
            </form>
        </div>
    );
}

export default Register;
